import React, { useEffect, useState } from 'react'
import { Button, Table } from 'react-bootstrap'
import { LinkContainer } from 'react-router-bootstrap'
import Loader from '../components/Loader'
import Message from '../components/Message'
import { useDispatch, useSelector } from 'react-redux'
import { listOrders } from '../actions/orderActions'

function OrderListScreen({ history }) {

    const dispatch = useDispatch();
    const current = new Date();
    const date = `${current.getFullYear()}-${current.getMonth() + 1}-${current.getDate()}`;

    const orderList = useSelector(state => state.orderList)
    const { loading, error, orders } = orderList
    const [todaysOrders, setTodaysOrders] = useState([])
    const userLogin = useSelector(state => state.userLogin)
    const { userInfo } = userLogin
    const todaysOrdershandler = (orders) => {
        if(orders){
            var newarray = [];
            for(var i=0; i<orders.length; i++){
                if(orders[i].createdAt.substring(0,10) === "2022-09-09"){
                    newarray.push(orders[i]);
                }
            }
            setTodaysOrders(newarray);
            console.log(todaysOrders);
        }
    }

    useEffect(() => {
        if (userInfo && userInfo.isAdmin) {
            dispatch(listOrders());
            todaysOrdershandler(orders);
        } else {
            history.push('/login')
        }
        todaysOrdershandler(orders);
    }, [dispatch, history, userInfo, todaysOrders])


    return (
        <div>
            <h1>Orders</h1>
            {loading
                ? <Loader />
                : error
                    ? (<Message variant='danger'>{error}</Message>)
                    : (
                        <Table striped bordered hover responsive className='table'>
                            <thead>
                                <tr>
                                    <th>ID</th>
                                    <th>User</th>
                                    <th>Date</th>
                                    <th>Total</th>
                                    <th>Paid</th>
                                    <th>Delivered</th>
                                    <th> </th>
                                </tr>

                            </thead>

                            <tbody>
                                {orders.map(order => (
                                    <tr key={order._id}>
                                        <td>{order._id}</td>
                                        <td>{order.user && order.user.name}</td>
                                        <td>{order.createdAt.substring(0, 10)}</td>
                                        <td>৳{order.totalPrice}</td>
                                        <td>{order.isPaid ? (
                                            order.paidAt.substring(0, 10)
                                        ) : (
                                            <i className='fas fa-check' style={{ color: 'red' }}></i>
                                        )}</td>

                                        <td>{order.isDelivered ? (
                                            order.deliveredAt.substring(0, 10)
                                        ) : (
                                            <i className='fas fa-check' style={{ color: 'red' }}></i>
                                        )}</td>

                                        <td>
                                            <LinkContainer to={`/order/${order._id}`}>
                                                <Button variant='dark' className='btn-sm'>
                                                    Details
                                                </Button>
                                            </LinkContainer>

                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </Table>
                    )
            }
        </div>
    )
}

export default OrderListScreen
